ebility::safe_left_join

function (x, y, by = NULL) 
{
    n_row = nrow(x)
    x = left_join(x, y %&gt;% mutate(..check.. = TRUE), by)
    if (nrow(x) &gt; n_row) {
        stop("Duplicated rows!")
    }
    if (any(ind &lt;- is.na(x[["..check.."]]))) {
        ind = sample(which(ind), min(10, sum(ind)))
        print(unique(x[ind, if (is.null(by)) 
            intersect(names(x), names(y))
        else names(nameifnot((by))), drop = FALSE]))
        stop("Failed to match one or more rows in x.")
    }
    x %&gt;% select(-..check..)
}
