ebility::clipboard

function (x = NULL, alloc = object.size(x), ...) 
{
    if (is.null(x)) {
        read.table("clipboard", header = TRUE, sep = "\t", ...)
    }
    else {
        if (!is.numeric(alloc)) {
            stop("alloc must be numeric.")
        }
        write.table(x, sprintf("clipboard-%d", alloc), sep = "\t", 
            qmethod = "double", row.names = FALSE, ...)
    }
}
